mapper.select:
select
zoo_id, name, description, create_Time, update_time, status
from zoo


        and zoo_id = #{zooId}


        and name = #{name}


        and description = #{description}


        and create_Time = #{createTime}


        and update_time = #{updateTime}


        and status = #{status}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from zoo


        and zoo_id = #{zooId}


        and name = #{name}


        and description = #{description}


        and create_Time = #{createTime}


        and update_time = #{updateTime}


        and status = #{status}
