mapper.select:
select
  zoo_id, zoo_name, description, create_time, update_time, is_deleted, creator, updater, deleter
from zoo

is_deleted = 0

        and zoo_id = #{zooId}


        and zoo_name = #{zooName}


        and description = #{description}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and creator = #{creator}


        and updater = #{updater}


        and deleter = #{deleter}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from zoo

        is_deleted = 0

        and zoo_id = #{zooId}


        and zoo_name = #{zooName}


        and description = #{description}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and creator = #{creator}


        and updater = #{updater}


        and deleter = #{deleter}
